
public class EvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Even Number from 1 to 100:");
		for(int i=1;i<=100;i++)
		{
			if(i%2==0)
			{
				System.out.println(i+"is an even number");	
			}
			else
				System.out.println(i);
		}

	}

}
